import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import CardComponent from './CardComponent';
import { DndContext, PointerSensor, closestCenter, useSensor } from '@dnd-kit/core';
import { SortableContext, arrayMove, verticalListSortingStrategy } from '@dnd-kit/sortable';

const App = () => {

const [items,setItems] = useState([
  {
    id:"1",
    title:"Featured-1",
    content:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, "
  },
  {
    id:"2",
    title:"Featured-2",
    content:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, "
  },
  {
    id:"3",
    title:"Featured-3",
    content:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, "
  },
])


const sensors = [useSensor(PointerSensor)];

const handleDragEnd = ({active,over})=>{
  if(active.id!==over.id){
    const oldIndex = items.findIndex(item=>item.id === active.id)
    const newIndex = items.findIndex(item=>item.id === over.id)

    arrayMove(items,oldIndex,newIndex)

  }
}


  return (
    <div className="row row-cols-1 row-cols-md-3 row-cols-lg-4 g-4 p-4">
      <DndContext sensors={sensors} collisionDetection={closestCenter} onDragEnd={(e)=>console.log(e)}>
      <SortableContext items={items.map(item=>item.id)} strategy={verticalListSortingStrategy}>
      {items.map(
        item=>
        <CardComponent {...item} key={item.id} />
      )}
      </SortableContext>
      </DndContext>

    </div>
  )
}

export default App
